import React, { useState } from 'react';
import {
  Container,
  Typography,
  Card,
  CardContent,
  Button,
  TextField,
  Grid,
  Box,
} from '@mui/material';
import { Bar } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

// Register Chart.js components
ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

const sampleChartData = {
  labels: ['Room 1', 'Room 2', 'Room 3', 'Room 4'],
  datasets: [
    {
      label: 'Avg COâ‚‚ (ppm)',
      data: [701.24, 652.88, 685.55, 670.42],
      backgroundColor: [
        '#4caf50',
        '#2196f3',
        '#ff9800',
        '#e91e63',
      ],
    },
  ],
};

const chartOptions = {
  responsive: true,
  plugins: {
    legend: { position: 'top' },
    title: {
      display: true,
      text: 'Average COâ‚‚ Levels by Room',
    },
  },
};

function App() {
  const [question, setQuestion] = useState('');

  const handleAsk = () => {
    console.log('User asked:', question);
    // Integrate with backend or AI agent here
  };

  return (
    <Container maxWidth="md" style={{ marginTop: '2rem' }}>
      <Typography variant="h3" align="center" gutterBottom>
        ðŸŒ¿ Air Quality AI Analyst
      </Typography>

      <Card sx={{ marginTop: 4, padding: 2 }}>
        <CardContent>
          <Typography variant="h6" color="textSecondary">
            Ask me anything like:
          </Typography>
          <Typography variant="body1" gutterBottom>
            "Which room had the highest average COâ‚‚ this week?"
          </Typography>

          <Grid container spacing={2} alignItems="center">
            <Grid item xs={9}>
              <TextField
                label="Enter your question"
                variant="outlined"
                fullWidth
                value={question}
                onChange={(e) => setQuestion(e.target.value)}
              />
            </Grid>
            <Grid item xs={3}>
              <Button
                variant="contained"
                color="primary"
                fullWidth
                onClick={handleAsk}
              >
                Ask
              </Button>
            </Grid>
          </Grid>
        </CardContent>
      </Card>

      <Box mt={5}>
        <Bar data={sampleChartData} options={chartOptions} />
      </Box>
    </Container>
  );
}

export default App;
